#[derive(Debug)]
pub enum GetRecipesError {
    InvalidPayloadFormat { payload: String },
    InvalidJWT,
    CouldntRetrieveRecipesFromAPI,
    NoDBConnectionFound,
    ErrorCheckingIfSessionIsValid,
    JWTExpired,
}

impl Display for GetRecipesError {
    fn fmt(&self, f: &mut std::fmt::Formatter<'_>) -> std::fmt::Result {
        write!(f, "{:?}", self)
    }
}

#[derive(Debug, Deserialize)]
struct GetRecipesPayload {
    token: String,
}

static ID: AtomicUsize = AtomicUsize::new(0);

pub async fn get_recipes(
    payload: Json<serde_json::Value>,
    client: Arc<Option<Client>>,
) -> Result<impl IntoResponse, ResponseError<GetRecipesError>> {
    let id = ID.fetch_add(1, std::sync::atomic::Ordering::AcqRel);
    let tracing_prefix = format!("/recipes - {}:", id);

    tracing::debug!("{} START", tracing_prefix);

    tracing::debug!("{} Parsing payload...", tracing_prefix);
    let GetRecipesPayload { token } = match serde_json::from_value(payload.0.clone()) {
        Ok(p) => p,
        Err(err) => {
            tracing::error!(
                "{} An error {:?} occurred while parsing the payload `{}`",
                tracing_prefix,
                err,
                payload.0
            );
            let error: ResponseError<_> = (
                StatusCode::BAD_REQUEST,
                GetRecipesError::InvalidPayloadFormat {
                    payload: payload.0.to_string(),
                },
            )
                .into();
            Err(error)?
        }
    };
    tracing::debug!("{} Payload parsed successfully!", tracing_prefix);

    tracing::debug!("{} Parsing JWT...", tracing_prefix);
    let token_info = match extract_jwt(APP_SECRET, &token) {
        Ok(t) => t,
        Err(err) => {
            tracing::error!(
                "{} An error `{:?}` occurred while extracting the JWT",
                tracing_prefix,
                err,
            );
            let error: ResponseError<_> =
                (StatusCode::BAD_REQUEST, GetRecipesError::InvalidJWT).into();
            Err(error)?
        }
    };
    tracing::debug!("{} JWT parsed successfully! `{:?}`", tracing_prefix, token_info);

    if let None = client.as_ref() {
        tracing::error!("{} No DB connection found!", tracing_prefix);
        let error: ResponseError<_> = (
            StatusCode::INTERNAL_SERVER_ERROR,
            GetRecipesError::NoDBConnectionFound,
        )
            .into();
        Err(error)?
    }

    match is_session_valid(token_info, client.as_ref().as_ref().unwrap()).await {
        Ok(true) => {}
        Ok(false) => {
            tracing::error!("{} The JWT already expired!", tracing_prefix);
            let error: ResponseError<_> =
                (StatusCode::UNAUTHORIZED, GetRecipesError::JWTExpired).into();
            Err(error)?
        }
        Err(err) => {
            tracing::error!(
                "{} An error `{:?}` occurred while checking if session is valid!",
                tracing_prefix,
                err
            );
            let error: ResponseError<_> =
                if let crate::IsSessionValidErrors::InternalDBError(_) = err {
                    (
                        StatusCode::INTERNAL_SERVER_ERROR,
                        GetRecipesError::ErrorCheckingIfSessionIsValid,
                    )
                } else {
                    (
                        StatusCode::BAD_REQUEST,
                        GetRecipesError::ErrorCheckingIfSessionIsValid,
                    )
                }
                .into();
            Err(error)?
        }
    };

    let recipes = match get_recipes_from_api().await {
        Ok(r) => r,
        Err(err) => {
            tracing::error!(
                "{} An error `{:?}` occurred while getting recipes from API!",
                tracing_prefix,
                err
            );
            let error: ResponseError<_> = (
                StatusCode::INTERNAL_SERVER_ERROR,
                GetRecipesError::CouldntRetrieveRecipesFromAPI,
            )
                .into();
            Err(error)?
        }
    };

    tracing::debug!("{} DONE", tracing_prefix);
    Ok(Json(recipes))
}

